import { useEffect, useState } from "react";
import API from "../services/api";

export default function Courses() {
  const [courses, setCourses] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [duration, setDuration] = useState("");

  const fetchCourses = () => {
    API.get("/courses").then(res => setCourses(res.data));
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  const addCourse = () => {
    if (!title || !description || !duration) return;

    API.post("/courses", { title, description, duration }).then(() => {
      setTitle("");
      setDescription("");
      setDuration("");
      fetchCourses();
    });
  };

  return (
    <div className="main">
      {/* Header */}
      <div className="page-header">
        <h1 className="page-title">Courses</h1>
        <button className="primary-btn" onClick={addCourse}>
          + Add Course
        </button>
      </div>

      <div className="courses-grid">
        {/* Add Course Form */}
        <div className="section">
          <h3>Add New Course</h3>

          <input
            placeholder="Course Title"
            value={title}
            onChange={e => setTitle(e.target.value)}
          />

          <input
            placeholder="Description"
            value={description}
            onChange={e => setDescription(e.target.value)}
          />

          <input
            placeholder="Duration (e.g. 4 hours)"
            value={duration}
            onChange={e => setDuration(e.target.value)}
          />

          <button className="primary-btn" onClick={addCourse}>
            Add Course
          </button>
        </div>

        {/* Course List */}
        <div className="section">
          <h3>Available Courses</h3>

          {courses.length === 0 && (
            <p className="muted-text">No courses added yet</p>
          )}

          {courses.map(course => (
            <div className="list-item" key={course._id}>
              <div>
                <h4>{course.title}</h4>
                <p className="muted-text">{course.description}</p>
              </div>

              <span className="status progress">
                {course.duration}
              </span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
