import { useEffect, useState } from "react";
import API from "../services/api";

export default function Lessons() {
  const [lessons, setLessons] = useState([]);
  const [title, setTitle] = useState("");

  const fetchLessons = () => {
    API.get("/lessons").then(res => setLessons(res.data));
  };

  useEffect(() => {
    fetchLessons();
  }, []);

  const addLesson = () => {
    if (!title) return;

    API.post("/lessons", { title }).then(() => {
      setTitle("");
      fetchLessons();
    });
  };

  const markCompleted = (id) => {
    API.put(`/lessons/${id}`, { status: "completed" })
      .then(fetchLessons);
  };

  return (
    <div className="main">
      {/* Header */}
      <div className="page-header">
        <h1 className="page-title">Lessons</h1>
      </div>

      <div className="lessons-grid">
        {/* Add Lesson */}
        <div className="section">
          <h3>Add New Lesson</h3>

          <form
            onSubmit={e => {
              e.preventDefault();
              addLesson();
            }}
          >
            <input
              value={title}
              onChange={e => setTitle(e.target.value)}
              placeholder="Lesson title"
            />

            <button className="primary-btn" type="submit">
              Add Lesson
            </button>
          </form>
        </div>

        {/* Lessons List */}
        <div className="section">
          <h3>Lesson List</h3>

          {lessons.length === 0 && (
            <p className="muted-text">No lessons added yet</p>
          )}

          {lessons.map(lesson => (
            <div className="list-item" key={lesson._id}>
              <div>
                <h4>{lesson.title}</h4>
                <span
                  className={`status ${
                    lesson.status === "completed"
                      ? "completed"
                      : "pending"
                  }`}
                >
                  {lesson.status}
                </span>
              </div>

              {lesson.status !== "completed" && (
                <button
                  className="secondary-btn"
                  onClick={() => markCompleted(lesson._id)}
                >
                  Mark as Completed
                </button>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
