import { useEffect, useState } from "react";
import API from "../services/api";

export default function Lessons() {
  const [lessons, setLessons] = useState([]);
  const [title, setTitle] = useState("");

  const fetchLessons = () => {
    API.get("/lessons").then(res => setLessons(res.data));
  };

  useEffect(() => {
    fetchLessons();
  }, []);

  const addLesson = () => {
    if (!title) return;
    API.post("/lessons", { title }).then(() => {
      setTitle("");
      fetchLessons();
    });
  };

  const markCompleted = (id) => {
    API.put(`/lessons/${id}`, { status: "completed" }).then(fetchLessons);
  };

  return (
    <div className="page">
      <h2>Lessons</h2>

      {/* Add Lesson */}
      <form
        onSubmit={e => {
          e.preventDefault();
          addLesson();
        }}
      >
        <input
          value={title}
          onChange={e => setTitle(e.target.value)}
          placeholder="Enter lesson title"
        />
        <button type="submit">Add Lesson</button>
      </form>

      {/* Lessons List */}
      <ul>
        {lessons.map(l => (
          <li key={l._id}>
            <span>{l.title}</span>

            <span className={`status ${l.status}`}>
              {l.status}
            </span>

            {l.status !== "completed" && (
              <button onClick={() => markCompleted(l._id)}>
                Complete
              </button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
