import { useEffect, useState } from "react";
import API from "../services/api";

export default function Courses() {
  const [courses, setCourses] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [duration, setDuration] = useState("");

  const fetchCourses = () => {
    API.get("/courses").then(res => setCourses(res.data));
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  const addCourse = () => {
    if (!title || !description || !duration) return;

    API.post("/courses", { title, description, duration }).then(() => {
      setTitle("");
      setDescription("");
      setDuration("");
      fetchCourses();
    });
  };

  return (
    <div className="main">
      {/* Header */}
      <div className="page-header">
        <h1 className="page-title">Courses</h1>
      </div>

      {/* Add Course Card */}
      <div className="card">
        <h3 className="card-title">Add New Course</h3>

        <div className="form-row">
          <input
            placeholder="Course Title"
            value={title}
            onChange={e => setTitle(e.target.value)}
          />
          <input
            placeholder="Duration (e.g. 4 hours)"
            value={duration}
            onChange={e => setDuration(e.target.value)}
          />
        </div>

        <textarea
          placeholder="Course Description"
          value={description}
          onChange={e => setDescription(e.target.value)}
        />

        <button className="primary-btn" onClick={addCourse}>
          + Add Course
        </button>
      </div>

      {/* Course List */}
      <div className="card">
        <h3 className="card-title">Available Courses</h3>

        {courses.length === 0 && (
          <p className="muted-text">No courses added yet</p>
        )}

        <div className="course-list">
          {courses.map(course => (
            <div className="course-item" key={course._id}>
              <div>
                <h4 className="course-title">{course.title}</h4>
                <p className="muted-text">{course.description}</p>
              </div>

              <span className="badge">
                {course.duration}
              </span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
